export const AttachFile = (props) => {
  const Url = process.env.REACT_APP_ATTACH_FILE
  const [value, setValue] = useState(props.initialValue)
  const [output, setOutput] = useState(null)
  const [selectedFile, setSelectedFile] = useState(props.selectedFile)
  const [form] = Form.useForm()
  const localField = form.getFieldValue(props.name)
  const tempRule = [
    {
      required: props.required,
      message: props.validationMessage || `Please attach ${props.label}`,
    },
  ]
  const localRules =
    props.rules instanceof Array ? [...tempRule, ...props.rules] : tempRule

//   useEffect(() => {
//     form.setFieldsValue({ [props.name]: value })
//   }, [props.form, props.name, value])
  useEffect(() => {
    if (!localField) {
      setValue(null)
    }
  }, [localField])

  useEffect(() => {
    setValue(props.initialValue)
  }, [props.initialValue])
  useEffect(() => {
    setSelectedFile(props.selectedFile)
    if (props.selectedFile) {
      props.selectedFile?.url
        ? setOutput(props.selectedFile?.url)
        : setOutput(URL.createObjectURL(props.selectedFile))
    }
  }, [props.selectedFile])
  const handelUrl = (item) => {
    const split = item.split("/")
    const url = split[split.length - 1]
    return url
  }
  return (
    <FormItem localRules={localRules} {...props}>
      <div>
        {!value ? (
          <>
            {!props.disabled && !props.viewDetails ? (
              <div
                className="centralize text-button"
                style={{
                  overflow: "hidden",
                  height: "32px",
                }}
              >
                <span
                  style={{
                    fontWeight: "bold",
                    fontSize: "16px",
                    marginRight: "3rem ",
                  }}
                >
                  <PaperClipOutlined size="small" />
                  Attach
                </span>
                <input
                  style={{
                    position: "absolute",
                    opacity: 0,
                    width: "100%",
                  }}
                  type="file"
                  onChange={(e) => {
                    setValue(e.target.files[0].name)
                    setOutput(URL.createObjectURL(e.target.files[0]))
                    setSelectedFile(e.target.files[0])
                    props.onChange instanceof Function && props.onChange(e)
                  }}
                />
              </div>
            ) : (
              <>
                {props.message ? (
                  <Popover content={props.message}>
                    <div className="centralize file-disabled">
                      <span style={{ fontWeight: "bold", fontSize: "16px" }}>
                        <PaperClipOutlined />{" "}
                        {props.viewDetails ? "No Doc." : "Attach"}
                      </span>
                    </div>
                  </Popover>
                ) : (
                  <div className="centralize file-disabled">
                    <span style={{ fontWeight: "bold", fontSize: "16px" }}>
                      <PaperClipOutlined />{" "}
                      {props.viewDetails ? "No Doc." : "Attach"}
                    </span>
                  </div>
                )}
              </>
            )}
          </>
        ) : (
          <div
            className="a-c file-uploaded"
            style={{
              overflow: "hidden",
              height: "32px",
              marginTop: props.hazard ? "25px" : "",
              cursor: "pointer",
              justifyContent: "space-around",
            }}
          >
            {props.initialValue === value && !selectedFile ? (
              <Popover
                style={{ borderRadius: "4px" }}
                content={handelUrl(value)}
              >
                <a
                  href={`${Url}${value}`}
                  className="centralize"
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    border: "none",
                    background: "transparent",
                    height: "100%",
                    width: "90%",
                    position: "relative",
                    overflow: "hidden",
                  }}
                >
                  {value.match(/.(jpg|jpeg|png|gif)$/i) ? (
                    <img
                      src={`${Url}${value}`}
                      style={{
                        height: "22px",
                        width: "22px",
                        marginRight: "5px",
                        position: "absolute",
                        left: "5px",
                      }}
                      alt=""
                    />
                  ) : (
                    <PaperClipOutlined
                      style={{
                        marginRight: "5px",
                        position: "absolute",
                        left: "5px",
                        cursor: "pointer",
                      }}
                    />
                  )}
                  <input
                    style={{
                      position: "absolute",
                      opacity: 0,
                      width: "100%",
                    }}
                    type="file"
                    onChange={(e) => {
                      setValue(e.target?.files[0]?.name)
                      setOutput(URL.createObjectURL(e.target.files[0]))
                      setSelectedFile(e.target.files[0])
                      props.onChange instanceof Function && props.onChange(e)
                    }}
                  />
                  <div
                    className="centralize"
                    style={{
                      padding: "2px 0 0 4px",
                      height: "25px",
                      width: "fit-content",
                      position: "absolute",
                      left: "30px",
                      display: "inline",
                    }}
                  >
                    {handelUrl(value)}
                  </div>
                </a>
              </Popover>
            ) : (
              <Popover content={selectedFile?.name || value}>
                <a
                  href={output}
                  className="centralize"
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    border: "none",
                    background: "transparent",
                    height: "100%",
                    width: "81%",
                    position: "relative",
                    overflow: "hidden",
                  }}
                >
                  {value?.match(/.(jpg|jpeg|png|gif)$/i) ||
                  selectedFile?.name?.match(/.(jpg|jpeg|png|gif)$/i) ? (
                    <img
                      src={output}
                      style={{
                        height: "22px",
                        width: "22px",
                        marginRight: "5px",
                        position: "absolute",
                        left: "5px",
                      }}
                      alt=""
                    />
                  ) : (
                    <PaperClipOutlined
                      style={{
                        marginRight: "5px",
                        position: "absolute",
                        left: "10px",
                      }}
                    />
                  )}
                  <input
                    style={{
                      position: "absolute",
                      opacity: 0,
                      width: "100%",
                    }}
                    type="file"
                    onChange={(e) => {
                      setValue(e.target?.files[0]?.name)
                      setOutput(URL.createObjectURL(e.target?.files[0]))
                      setSelectedFile(e.target.files[0])
                      props.onChange instanceof Function && props.onChange(e)
                    }}
                  />
                  <div
                    className="centralize"
                    style={{
                      padding: "1px 0 0 4px",
                      height: "25px",
                      width: "fit-content",
                      position: "absolute",
                      left: "30px",
                      display: "inline",
                    }}
                  >
                    {selectedFile?.name || value}
                  </div>
                </a>
              </Popover>
            )}
            {!props.viewDetails && (
              <div style={{ color: "red" }}>
                <DeleteFilled
                  color="red"
                  onClick={(e) => {
                    e.preventDefault()
                    setValue(null)
                    props.onDelete && props.onDelete(e)
                  }}
                />
              </div>
            )}
          </div>
        )}
      </div>
    </FormItem>
  )
}

